      bar: @ui.json_bar.val()

    @model.save(title: title, json: json)
Backbone = require('backbone')

module.exports = class SearchFormView extends Backbone.View
  className: 'new-search'

  template: require('../templates/SearchForm')

  events:
    'submit form': 'onSubmit'

  render: ->
    @$el.html(@template())

  onSubmit: (e) ->
    e.preventDefault()

    name = @$('input[name=name]').val().trim()

    if name
      query = name
      @trigger('create', name: name, query: name)
      @$('form')[0].reset()
_ = require('lodash')
Backbone = require('backbone')

# An search. Has a name and some terms.
module.exports = class Search extends Backbone.Model
  defaults:
    name: ''
    query: ''
    nDocuments: null
    error: null

  parse: (json) -> _.extend({ id: json.id }, json.json)
  toJSON: -> { json: _.omit(@attributes, 'id') }

  startRefresh: ->
    server = @collection.server
    documentSetId = @collection.documentSetId
    query = @get('query')

    url = "#{server}/api/v1/document-sets/#{documentSetId}/documents?fields=id&q=#{encodeURIComponent(query)}"

    Backbone.ajax
      url: url
      success: (ids) =>
        @save(nDocuments: ids.length, error: null)
      error: (err) =>
        @save(nDocuments: null, error: err)
